import Timer from "../domain/Timer";

/**
 * Servicio que gestiona la lgica de aplicacin del cronmetro.
 * Acta como intermediario entre el dominio y la interfaz de usuario.
 */
class TimerService {
  constructor() {
    this.timer = new Timer();
    this.onTick = null;
  }

  /**
   * Inicia el cronmetro y configura la actualizacin peridica.
   * @param {Function} callback - Funcin a llamar en cada actualizacin.
   */
  startTimer(callback) {
    this.onTick = callback;
    this.timer.start();

    if (!this.intervalId) {
      this.intervalId = setInterval(() => {
        if (this.onTick) {
          this.onTick(this.getFormattedTime());
        }
      }, 10); // Actualiza cada 10ms para mostrar milisegundos
    }
  }

  /**
   * Pausa el cronmetro.
   */
  pauseTimer() {
    this.timer.pause();
  }

  /**
   * Reinicia el cronmetro a cero.
   */
  resetTimer() {
    this.timer.reset();
    if (this.onTick) {
      this.onTick(this.getFormattedTime());
    }
  }

  /**
   * Detiene completamente el cronmetro y limpia el intervalo.
   */
  stopTimer() {
    this.timer.pause();
    if (this.intervalId) {
      clearInterval(this.intervalId);
      this.intervalId = null;
    }
  }

  /**
   * Verifica si el cronmetro est en marcha.
   * @returns {boolean} true si est en marcha, false si no.
   */
  isRunning() {
    return this.timer.isRunning();
  }

  /**
   * Obtiene el tiempo actual formateado.
   * @returns {Object} Objeto con horas, minutos, segundos y milisegundos.
   */
  getFormattedTime() {
    const totalMilliseconds = this.timer.getTime();

    const milliseconds = totalMilliseconds % 1000;
    const totalSeconds = Math.floor(totalMilliseconds / 1000);
    const seconds = totalSeconds % 60;
    const totalMinutes = Math.floor(totalSeconds / 60);
    const minutes = totalMinutes % 60;
    const hours = Math.floor(totalMinutes / 60);

    return {
      hours,
      minutes,
      seconds,
      milliseconds,
      formatted: {
        hours: hours.toString().padStart(2, "0"),
        minutes: minutes.toString().padStart(2, "0"),
        seconds: seconds.toString().padStart(2, "0"),
        milliseconds: {
          tenths: Math.floor(milliseconds / 100),
          hundredths: Math.floor(milliseconds / 10) % 10,
          thousandths: milliseconds % 10,
        },
      },
    };
  }
}

export default TimerService;
